import React, { useState } from "react";
import TodoForm from "./Components/TodoForm"; 
import TodoList from "./Components/TodoList";
import './main.css';


function App(){
  const [todos, setodos] = useState([]);
  const addTodo = (text) => {
    const newtodo = {id: Date.now(), text, completed:false };
    setodos([...todos,newtodo]);
  };

  const toggleComplete = (id) => {
    setodos(
      todos.map((todo) => todo.id === id ? 
      {...todos, completed: ! todo.completed } : todo)
    );
  };

  const deleteTodo = (id) =>{
    setodos(todos.filter((todo) => todo.id !== id));
  };

  return(
    <div className="bg-grey-100 min-h-screen flex flex-col items-center p-6">
      <h1 className="text-4x1 font-bold text-blue-600">To-DO App</h1>
      <TodoForm addTodo={addTodo}/>
      <TodoList todos={todos}
      toggleComplete={toggleComplete}
      deleteTodo={deleteTodo}/>
    </div>
  )
}

export default App;
